import React, { useState } from 'react';
import './login.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();

        // Reguliere expressie voor wachtwoordvalidatie
        const passwordRegex = /^(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*(),.?":{}|<>]).{16,}$/;

        if (!passwordRegex.test(password)) {
            setError('Password must be at least 16 characters long and contain at least one uppercase letter, one number, and one special character.');
            return;
        }

        // Handle login logic here
        console.log('Email:', email);
        console.log('Password:', password);
        setError('');  // Clear error if validation passes
    };

    return (
        <div className="login-container">
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="email">Email:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        placeholder="Enter your email"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        placeholder="Enter your password"
                    />
                </div>
                {error && <p className="error-message">{error}</p>}
                <button type="submit" className="login-button">Login</button>
            </form>
            <div className="links">
                <a href="/forgot-password">Forgot your password?</a>
                <a href="/register">Don't have an account? Register</a>
            </div>
        </div>
    );
};

export default Login;
