import React, { useState } from 'react';
import './profiel-aanmaken.css';

const ProfielAanmaken = () => {
  const [optionalFields, setOptionalFields] = useState({
    opleidingsniveau: false,
    talen: false,
    beroep: false,
    hobbies: false,
    sportiviteit: false,
    cultuur: false,
    stijl: false,
    uitgaansFrequentie: false,
    muziekstijlen: false,
    alcoholFrequentie: false,
    huisdieren: false,
    kinderen: false,
    kinderwens: false,
    postcode: false,
    relatieOpenVoor: false,
  });

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    setOptionalFields((prev) => ({
      ...prev,
      [name]: checked,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const selectedFields = Object.keys(optionalFields).filter(
      (key) => optionalFields[key]
    );

    if (selectedFields.length < 2) {
      alert('Kies minimaal 2 optionele velden.');
      return;
    }
    console.log('Geselecteerde optionele velden:', selectedFields);
  };

  return (
    <div className="profile-container">
      <h2>Profiel Aanmaken</h2>
      <form onSubmit={handleSubmit}>
        {/* Andere vaste velden */}
        <div className="form-group">
          <label htmlFor="nickname">Nickname:</label>
          <input type="text" id="nickname" name="nickname" required />
        </div>
        {/* Optionele velden */}
        <div className="optional-section">
          <h3>Kies minimaal 2 optionele velden:</h3>
          {Object.keys(optionalFields).map((field) => (
            <div key={field} className="form-group">
              <label>
                <input
                  type="checkbox"
                  name={field}
                  checked={optionalFields[field]}
                  onChange={handleCheckboxChange}
                />
                {field
                  .replace(/([A-Z])/g, ' $1')
                  .replace(/^./, (str) => str.toUpperCase())}
              </label>
            </div>
          ))}
        </div>
        <button type="submit">Bevestigen</button>
      </form>
    </div>
  );
};

export default ProfielAanmaken;
