import React, { useState } from 'react';
import './Registreren.css';

const Registreren = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [birthdate, setBirthdate] = useState('');
  const [error, setError] = useState('');

  // Functie om te berekenen of de gebruiker 18+ is
  const isAdult = (birthdate) => {
    const today = new Date();
    const birthDateObj = new Date(birthdate);
    const age = today.getFullYear() - birthDateObj.getFullYear();
    const monthDifference = today.getMonth() - birthDateObj.getMonth();
    if (monthDifference < 0 || (monthDifference === 0 && today.getDate() < birthDateObj.getDate())) {
      return age - 1; // Als de verjaardag dit jaar nog niet is geweest
    }
    return age;
  };

  // Verwerking van het formulier
  const handleSubmit = (e) => {
    e.preventDefault();

    // Check of de gebruiker 18+ is
    const age = isAdult(birthdate);
    if (age < 18) {
      setError('Je moet 18 jaar of ouder zijn om je te registreren.');
    } else {
      setError('');
      // Voer hier je registratieproces uit
      console.log('Email:', email);
      console.log('Password:', password);
      console.log('Geboortedatum:', birthdate);
      // Voeg je logica voor registratie toe (API-aanroep, formulier opslaan, etc.)
    }
  };

  return (
    <div className="login-container">
      <h2>Registreren</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="email">E-mail:</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Wachtwoord:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="birthdate">Geboortedatum:</label>
          <input
            type="date"
            id="birthdate"
            value={birthdate}
            onChange={(e) => setBirthdate(e.target.value)}
            required
          />
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit">Registreren</button>
      </form>
    </div>
  );
};

export default Registreren;
