import React, { useState } from 'react';
import './profiel-aanmaken.css';

const ProfielAanmaken = () => {
  const [formData, setFormData] = useState({
    nickname: '',
    oneliner: '',
    relatieWaardering: '',
    partnerZoekcriteria: '',
    gezichtsFoto: null,
    gender: '',
    interestedIn: '',
    birthdate: '',
    optionalFields: [],
    termsAccepted: false,
  });

  const optionalOptions = [
    'Opleidingsniveau',
    'Talen die je spreekt',
    'Beroep',
    'Hobbies',
    'Sportiviteit (hoe vaak?)',
    'Cultuur',
    'Mijn stijl',
    'Uitgaansfrequentie',
    'Muziekstijlen',
    'Alcoholgebruik (hoe vaak?)',
    'Huisdieren',
    'Kinderen',
    'Kinderwens',
    'Postcode',
    'Relatie waar je voor openstaat',
  ];

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;
    if (type === 'checkbox') {
      if (optionalOptions.includes(value)) {
        setFormData((prev) => {
          const updatedFields = checked
            ? [...prev.optionalFields, value]
            : prev.optionalFields.filter((field) => field !== value);
          return { ...prev, optionalFields: updatedFields };
        });
      } else {
        setFormData({ ...formData, [name]: checked });
      }
    } else if (type === 'file') {
      setFormData({ ...formData, [name]: files[0] });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.optionalFields.length < 2) {
      alert('Selecteer minimaal 2 optionele velden.');
      return;
    }
    if (!formData.termsAccepted) {
      alert('Je moet akkoord gaan met de algemene voorwaarden.');
      return;
    }
    console.log('Ingevoerde gegevens:', formData);
    // Voeg logica toe om de gegevens te verwerken
  };

  return (
    <div className="profile-container">
      <h2>Profiel Aanmaken</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="nickname">Nickname:</label>
          <input
            type="text"
            id="nickname"
            name="nickname"
            value={formData.nickname}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="oneliner">Oneliner:</label>
          <input
            type="text"
            id="oneliner"
            name="oneliner"
            value={formData.oneliner}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="relatieWaardering">Hoe waardeer je een relatie?</label>
          <input
            type="text"
            id="relatieWaardering"
            name="relatieWaardering"
            value={formData.relatieWaardering}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="partnerZoekcriteria">Wat zoek je in een partner?</label>
          <textarea
            id="partnerZoekcriteria"
            name="partnerZoekcriteria"
            value={formData.partnerZoekcriteria}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="gezichtsFoto">Gezichtsfoto:</label>
          <input
            type="file"
            id="gezichtsFoto"
            name="gezichtsFoto"
            accept="image/*"
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="gender">Geslacht:</label>
          <select
            id="gender"
            name="gender"
            value={formData.gender}
            onChange={handleChange}
            required
          >
            <option value="">Selecteer je geslacht</option>
            <option value="male">Man</option>
            <option value="female">Vrouw</option>
            <option value="other">Anders</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="interestedIn">Welk geslacht interesseert je?</label>
          <select
            id="interestedIn"
            name="interestedIn"
            value={formData.interestedIn}
            onChange={handleChange}
            required
          >
            <option value="">Selecteer</option>
            <option value="male">Man</option>
            <option value="female">Vrouw</option>
            <option value="other">Anders</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="birthdate">Geboortedatum:</label>
          <input
            type="date"
            id="birthdate"
            name="birthdate"
            value={formData.birthdate}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group checkbox-group">
          <label>Optionele velden (selecteer minimaal 2):</label>
          {optionalOptions.map((option) => (
            <div key={option}>
              <input
                type="checkbox"
                id={option}
                value={option}
                checked={formData.optionalFields.includes(option)}
                onChange={handleChange}
              />
              <label htmlFor={option}>{option}</label>
            </div>
          ))}
        </div>
        <div className="form-group checkbox-group">
          <input
            type="checkbox"
            id="termsAccepted"
            name="termsAccepted"
            checked={formData.termsAccepted}
            onChange={handleChange}
          />
          <label htmlFor="termsAccepted">
            Ik ga akkoord met de <a href="/terms">algemene voorwaarden</a> en <a href="/privacy">privacyregels</a>.
          </label>
        </div>
        <button type="submit">Profiel Aanmaken</button>
      </form>
    </div>
  );
};

export default ProfielAanmaken;
