import React, { useState } from 'react';
import './Registreren.css';  // Specifieke CSS voor registratie

const Registreren = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();

        // Reguliere expressie voor wachtwoordvalidatie
        const passwordRegex = /^(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*(),.?":{}|<>]).{16,}$/;

        if (!passwordRegex.test(password)) {
            setError('Wachtwoord voldoet niet aan wachtwoord criteria');
            return;
        }

        // Handle registration logic here
        console.log('Email:', email);
        console.log('Password:', password);
        setError('');  // Clear error if validation passes
    };

    return (
        <div className="login-container">
            <h2>Registreren</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label htmlFor="email">E-mail:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        placeholder="Voer je e-mail in"
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Wachtwoord:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        placeholder="Voer je wachtwoord in"
                    />
                </div>
                {error && <p className="error-message">{error}</p>}
                <button type="submit" className="login-button">Registreren</button>
            </form>
            <div className="links">
                <a href="/login">Heb je al een account? Inloggen</a>
            </div>
        </div>
    );
};

export default Registreren;
