import React, { useState } from 'react';
import './profiel-aanmaken.css';

const ProfielAanmaken = () => {
  const [formData, setFormData] = useState({
    nickname: '',
    oneliner: '',
    relationshipPreference: '',
    lookingFor: '',
    profilePhoto: null,
    gender: '',
    interestedIn: '',
    birthDate: '',
    optionalFields: {
      educationLevel: '',
      languages: [],
      profession: '',
      hobbies: [],
      sportsFrequency: '',
      culture: '',
      style: '',
      goingOutFrequency: '',
      musicStyles: '',
      alcoholFrequency: '',
      pets: '',
      children: '',
      desireForChildren: '',
      postcode: '',
      relationshipType: '',
    },
  });

  const [selectedOptions, setSelectedOptions] = useState([]);
  const [error, setError] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleOptionalChange = (field, value) => {
    setFormData({
      ...formData,
      optionalFields: { ...formData.optionalFields, [field]: value },
    });
  };

  const handleMultiSelect = (field, options) => {
    const selected = Array.from(options, (option) => option.value);
    setSelectedOptions(selected);
    setFormData({
      ...formData,
      optionalFields: { ...formData.optionalFields, [field]: selected },
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validate that at least 2 optional fields are selected
    if (selectedOptions.length < 2) {
      setError('Je moet minimaal 2 optionele velden selecteren.');
      return;
    }

    console.log('Submitted data:', formData);
    setError('');
    // Hier kun je een redirect toevoegen naar een andere pagina
  };

  return (
    <div className="profile-container">
      <h2>Profiel Aanmaken</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="nickname">Nickname:</label>
          <input
            type="text"
            id="nickname"
            name="nickname"
            value={formData.nickname}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="oneliner">Oneliner:</label>
          <textarea
            id="oneliner"
            name="oneliner"
            value={formData.oneliner}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="relationshipPreference">Relatiewaarde:</label>
          <select
            id="relationshipPreference"
            name="relationshipPreference"
            value={formData.relationshipPreference}
            onChange={handleInputChange}
            required
          >
            <option value="">Selecteer...</option>
            <option value="serieuze_relatie">Serieuze Relatie</option>
            <option value="vriendschap">Vriendschap</option>
            <option value="open_relatie">Open Relatie</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="gender">Geslacht:</label>
          <select
            id="gender"
            name="gender"
            value={formData.gender}
            onChange={handleInputChange}
            required
          >
            <option value="">Selecteer...</option>
            <option value="man">Man</option>
            <option value="vrouw">Vrouw</option>
            <option value="anders">Anders</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="interestedIn">Geïnteresseerd in:</label>
          <select
            id="interestedIn"
            name="interestedIn"
            value={formData.interestedIn}
            onChange={handleInputChange}
            required
          >
            <option value="">Selecteer...</option>
            <option value="mannen">Mannen</option>
            <option value="vrouwen">Vrouwen</option>
            <option value="beide">Beide</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="birthDate">Geboortedatum:</label>
          <input
            type="date"
            id="birthDate"
            name="birthDate"
            value={formData.birthDate}
            onChange={handleInputChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="languages">Talen die je spreekt:</label>
          <select
            id="languages"
            name="languages"
            multiple
            value={formData.optionalFields.languages}
            onChange={(e) => handleMultiSelect('languages', e.target.selectedOptions)}
          >
            <option value="nederlands">Nederlands</option>
            <option value="engels">Engels</option>
            <option value="spaans">Spaans</option>
            <option value="frans">Frans</option>
            <option value="duits">Duits</option>
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="hobbies">Hobbies:</label>
          <textarea
            id="hobbies"
            name="hobbies"
            value={formData.optionalFields.hobbies}
            onChange={(e) => handleOptionalChange('hobbies', e.target.value.split(','))}
            placeholder="Bijv: Lezen, Schilderen"
          />
        </div>

        {error && <p className="error">{error}</p>}

        <button type="submit">Profiel Aanmaken</button>
      </form>
    </div>
  );
};

export default ProfielAanmaken;
